/*filetype:php*/
$order=setValue(array($_REQUEST['order'],'name'));
$recs=getDBRecords(array('-table'=&gt;&quot;wcart_products&quot;,'active'=&gt;1,'-order'=&gt;$order));
echo '&lt;div class=&quot;product_list&quot;&gt;'.&quot;\n&quot;;
foreach($recs as $rec){
	echo '	&lt;li&gt;'.&quot;\n&quot;;
	echo '		&lt;div class=&quot;w_pad&quot;&gt;'.&quot;\n&quot;;
	if(strlen($rec['image'])){
    	echo '			&lt;img border=&quot;0&quot; src=&quot;'.$rec['image'].'&quot; /&gt;'.&quot;\n&quot;;
	}
	echo '			&lt;div class=&quot;name&quot;&gt;'.$rec['name'].'&lt;/div&gt;'.&quot;\n&quot;;
	echo '			&lt;div class=&quot;description&quot;&gt;'.$rec['description'].'&lt;/div&gt;'.&quot;\n&quot;;
	echo '			&lt;div class=&quot;details&quot;&gt;'.$rec['details'].'&lt;/div&gt;'.&quot;\n&quot;;
	echo '			&lt;div class=&quot;price&quot;&gt;$ '.$rec['price'].'&lt;/div&gt;'.&quot;\n&quot;;
	echo '			&lt;div class=&quot;cart&quot;&gt;'.&quot;\n&quot;;
	echo '				&lt;input type=&quot;text&quot; size=&quot;1&quot; id=&quot;&quot; value=&quot;1&quot;&gt;'.&quot;\n&quot;;
	echo '				&lt;a href=&quot;#&quot; onclick=&quot;return ajaxGet(\'/php/wcart.php\',\'wcart\',\'c=add&amp;qty=1&amp;pid='.$rec['_id'].'\');&quot; class=&quot;w_button_gray&quot;&gt;Add to cart&lt;/a&gt;'.&quot;\n&quot;;
	echo '			&lt;/div&gt;'.&quot;\n&quot;;
	echo '		&lt;/div&gt;'.&quot;\n&quot;;
	echo '	&lt;/li&gt;'.&quot;\n&quot;;
}
echo '&lt;/div&gt;'.&quot;\n&quot;;
echo '&lt;div class=&quot;clearall&quot;&gt;&lt;/div&gt;'.&quot;\n&quot;;
echo '&lt;div id=&quot;wcart&quot; style=&quot;position:absolute;display:none;&quot; onshow=&quot;center&quot;&gt;&lt;/div&gt;'.&quot;\n&quot;;
?&gt;

